	--,('%message', 'Message', @Message)
	--,('%p', 'Level', @LogLevelName)
	--,('%r', 'TimeStamp', CONCAT(SYSDATETIME(),''))
	--,('%', 'SessionId', CONCAT(@@SPID, ''))
	--,('%thread', 'SessionId', CONCAT(@@SPID, ''))
	--,('%spid', 'SessionId', CONCAT(@@SPID, ''))
